import { Component } from '@angular/core';

@Component({
  selector: 'app-home',
  templateUrl: './home.component.html',
  styleUrls: ['./home.component.css']
})
export class HomeComponent {
  currentIndex:number=0;
  images:string[] = ["11_Prime_5G.webp","Note_Pro_5G.webp","Note_Pro_12Plus.webp"];
  image_src:string=this.images[0];

  timer:number=setInterval(()=>{
    this.currentIndex=Math.round(Math.random()*4);
    this.image_src = "../assets/images/banner/"+this.images[this.currentIndex];    
  },2000);

  changeImage(arrowType:string):void{
    if(arrowType=="right")
      {          
          this.currentIndex = this.currentIndex==2?0:++this.currentIndex;
      }
      else if(arrowType=="left")
      {
        this.currentIndex = this.currentIndex==0?2:--this.currentIndex;          
      }
      this.image_src = "../assets/images/banner/"+this.images[this.currentIndex];
  }
}
<div id="slide-area">
    <div id="slide-show">                
        <img [src]="image_src" id="img1" class="slide">
        <img src="../assets/images/banner/left_arrow.png" id="left_icon" class="icon" onclick="changeImage('left')">
        <img src="../assets/images/banner/right_arrow.png" id="right_icon" class="icon" onclick="changeImage('right')">
    </div>
</div>
