import { Component } from '@angular/core';

@Component({
  selector: 'app-home',
  templateUrl: './home.component.html',
  styleUrls: ['./home.component.css']
})
export class HomeComponent {
  currentIndex=0;
  images = ["11_Prime_5G.webp","Note_Pro_5G.webp","Note_Pro_12Plus.webp"];

  setInterval(function(){
      currentIndex=Math.round(Math.random()*4);
      image_src = "images/"+images[currentIndex];
      console.log(image_src);
      document.getElementById("img1").src = image_src;
  },2000);

  function changeImage(arrowType)
  {
      if(arrowType=="right")
      {
          //currentIndex = currentIndex==4?0:(currentIndex+1);
          currentIndex = currentIndex==4?0:++currentIndex;
      }
      else if(arrowType=="left")
      {
          //currentIndex = currentIndex==0?4:(currentIndex-1);
          currentIndex = currentIndex==0?4:--currentIndex;
      }
      image_src = "images/"+images[currentIndex];
      console.log(image_src);
      document.getElementById("img1").src = image_src;
  }
}
<div id="slide-show">                
    <img src="../assets/images/banner/11_Prime_5G.webp" id="img1" class="slide">
    <img src="images/banner/left_arrow.png" id="left_icon" class="icon" onclick="changeImage('left')">
    <img src="images/banner/right_arrow.png" id="right_icon" class="icon" onclick="changeImage('right')">
</div>
