import { Component } from '@angular/core';

@Component({
  selector: 'app-home',
  templateUrl: './home.component.html',
  styleUrls: ['./home.component.css']
})
export class HomeComponent {
  currentIndex:number=0;
  images:string[] = ["11_Prime_5G.webp","Note_Pro_5G.webp","Note_Pro_12Plus.webp"];

  const timer:number=setInterval(()=>{
    this.currentIndex=Math.round(Math.random()*4);
    let image_src = "images/"+images[this.currentIndex];
    console.log(image_src);
    document.getElementById("img1").src = image_src;
  },2000);

}
<div id="slide-show">                
    <img src="../assets/images/banner/11_Prime_5G.webp" id="img1" class="slide">
    <img src="images/banner/left_arrow.png" id="left_icon" class="icon" onclick="changeImage('left')">
    <img src="images/banner/right_arrow.png" id="right_icon" class="icon" onclick="changeImage('right')">
</div>
