import { Component } from '@angular/core';

@Component({
  selector: 'app-home',
  templateUrl: './home.component.html',
  styleUrls: ['./home.component.css']
})
export class HomeComponent {
  currentIndex:number=0;
  images:string[] = ["11_Prime_5G.webp","Note_Pro_5G.webp","Note_Pro_12Plus.webp"];
  image_src:string=this.images[0];

  const timer:number=setInterval(()=>{
    this.currentIndex=Math.round(Math.random()*4);
    this.image_src = "../assets/images/banner/"+this.images[this.currentIndex];    
  },2000);

}
