import { Component } from '@angular/core';
import { banner } from 'src/app/model/model';
import { BannerService } from 'src/app/services/banner.service';

@Component({
  selector: 'app-home',
  templateUrl: './home.component.html',
  styleUrls: ['./home.component.css']
})
export class HomeComponent {
  banner:banner[]=[];
  currentIndex:number=0;
  currentBanner:banner={id:0,title:"",tag:"",image:"",mrp:0,price:0};  
  image_src:string="11_Prime_5G.webp";

  timer:number=setInterval(()=>{
    this.currentIndex=Math.round(Math.random()*2);
    this.currentIndex=this.currentIndex>2?2:this.currentIndex;
    this.currentBanner = this.banner[this.currentIndex];
    this.image_src = "../assets/images/banner/"+this.currentBanner.image;    
  },2000);

  changeImage(arrowType:string):void{
    if(arrowType=="right")
      {          
          this.currentIndex = this.currentIndex==2?0:++this.currentIndex;
      }
      else if(arrowType=="left")
      {
        this.currentIndex = this.currentIndex==0?2:--this.currentIndex;          
      }      
      this.currentBanner = this.banner[this.currentIndex];
      this.image_src = "../assets/images/banner/"+this.currentBanner.image;
  }


  constructor(private svcBanner:BannerService){

  }

  ngOnInit():void{
    this.svcBanner.getBanner().subscribe({
        next:(data:banner[])=>this.banner=data,
        error:(ex:any)=>{this.banner=[]; console.error(ex)}
      }
    );
  }
}
<div id="slide-area">
    <div id="slide-show">
        <div class="site-banner__info">
            <h2 class="site-banner__title">{{currentBanner.title}}</h2>
            <h4 class="site-banner__subtitle">{{currentBanner.tag}}</h4>
            <br>
            <div class=".site-banner__price">
                <span style="margin-left: 10px;font-size: 40px;"><strong>{{currentBanner.price | currency:'INR':true:'1.0-0'}}</strong></span>
                <span style="margin-left: 10px;font-size: 28px;color:#999;text-decoration: line-through;">{{currentBanner.mrp | currency:'INR':true:'1.0-0'}}</span>
            </div>
           <br>
            <span><button mat-stroked-button color="primary">Buy now</button></span>
            <span style="margin-left:20px"><button mat-flat-button color="primary">More details</button></span>
        </div>                
        <img [src]="image_src" id="img1" class="slide">
        <img src="../assets/images/banner/left_arrow.png" id="left_icon" class="icon" onclick="changeImage('left')">
        <img src="../assets/images/banner/right_arrow.png" id="right_icon" class="icon" onclick="changeImage('right')">
    </div>
</div>
